import puppeteer from 'puppeteer';

export default class ScrapperFifaArena {
  static async getJSONData() {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    await page.goto('http://stats.cyberarena.live/results.aspx?tab=fifa21', {waitUntil: 'networkidle2'});
    const content = await page.evaluate(() => {
      const lines = [];
      const rows = Array.from(document.querySelectorAll('div > table > tbody > tr + tr'));

      rows.map((item, index) => {
        if (index > 0 && item.innerText.split(':').length === 4) {
          const arrayLine = item.innerText.split('\t');
          const link = item.querySelector('td > a');
          lines.push({
            time: arrayLine[0],
            video: link.getAttribute('href'),
            teamA: {
              team: arrayLine[1],
              user: arrayLine[2],
              firstHalf: arrayLine[3].split(' ')[0],
              secondHalf: arrayLine[3].split(' ')[1],
            },
            teamB: {
              team: arrayLine[4],
              user: arrayLine[5],
              firstHalf: arrayLine[3].split(' ')[0],
              secondHalf: arrayLine[3].split(' ')[1],
            },
          });
        }
      });

      return lines;
    });
    await browser.close();

    return content;

  }
};
