import Scrapper from './Scrapper';
import FactoryFifaArena from './FactoryFifaArena';
import Util from '@brother.bet/Util';
import Database from '@brother.bet/Database';
import Api from '@brother.bet/Api';

export default class ServiceFifaArena {

  constructor() {
    this.baseUrl = 'https://brother.bet';
    this.urlSource = 'http://stats.cyberarena.live/results.aspx?tab=fifa21';
    this.localDatabase = this.getDatabase(today);
    this.api = new Api(this.baseUrl).api;
    this.database = new Database(this.api);
    this.delay = Util.delay;
  }

  static async updateFifaArena(){
    try {
      const contentHTML = await Scrapper.getPageHTML(this.urlSource, this.today);
      const data = await FactoryFifaArena.getGamesResult(contentHTML);
      console.log(localDatabase)
      return {data: {games: data}, code: 200};
    } catch (error) {
      return { data : {message: 'Estamos com indisponibilidade no momento, tente novamente mais tarde!',
              ...error},
              code: 500
            };
    }
  }

  getToday() {
    const d = new Date();
    const myTZO = -180;
    const myNewDate = new Date(d.getTime() + 60000 * (d.getTimezoneOffset() - myTZO));
    const today = myNewDate.toISOString().slice(0, 10);
    return today;
  }
}
