import puppeteer from 'puppeteer'

export default class Scrapper {
  static async getPageHTML(url, date = null) {
    const browser = await puppeteer.launch()
    const page = await browser.newPage()
    await page.goto(url, { waitUntil: 'networkidle2' })

      if (date) {
        await page.evaluate(() => {
          document.getElementById('tb_date').setAttribute('value', date)
          var ev = input.createEvent('KeyboardEvent')
          ev.initKeyEvent('keydown', true, true, window, false, false, false, false, 13, 0)
          input.dispatchEvent(ev)
        })
      }

    const content = await page.content()

    await browser.close()
    return content
  }

}
