import puppeteer from 'puppeteer'

export default class Scrapper {
  static async getPageHTML(url, date = null) {
    const browser = await puppeteer.launch()
    const page = await browser.newPage()
    await page.goto(url, { waitUntil: 'networkidle2' })

    if(date){
      this.changeDateFifaArena(date, page);
    }

    const content = await page.content();

    await browser.close()
    return content
  }

  static async getJSONData(url, date = null) {
    const browser = await puppeteer.launch();
    const page = await browser.newPage();
    await page.goto('http://stats.cyberarena.live/results.aspx?tab=fifa21', {waitUntil: 'networkidle2'});

    if(date){
      await page.evaluate(() => {
        const input = document.getElementById("tb_date").setAttribute('value', date);
        var ev = input.createEvent('KeyboardEvent');
        ev.initKeyEvent('keydown', true, true, window, false, false, false, false, 13, 0);
        input.body.dispatchEvent(ev);
      })
    }

    const content = await page.content();
    await browser.close();

    return content;
  }

  static async changeDateFifaArena(day = null, page) {
    const id = 'tb_date';
    try {
      const datePage = await page.findElement(By.name(id)).getAttribute('value');
      if (datePage !== day) {
        driver.executeScript(`document.getElementById('${id}').value='${day}'`);
        await driver.findElement(By.name(id)).sendKeys(Key.RETURN);
      }
    } finally {
      return driver.getPageSource();
    }
  }
}
