import puppeteer from 'puppeteer'

export default class Scrapper {
  static async getPageHTML(url, date = null) {
    const browser = await puppeteer.launch()
    const page = await browser.newPage()
    let content = null

    await page.goto(url, { waitUntil: 'networkidle2' })

    if (date) {
      await page.evaluate((date) => {
        document.querySelector('#tb_date').value = date
        document.querySelector('body').click()
      })
      await page.waitFor('input[name=tb_date]')
      await page.$eval('input[name=tb_date]', (el, date) => (el.value = date), date)
      content = await page.content()
      await setTimeout(async function () {}, 3000)
    }
    content = await page.content()
    console.log(content)
    await browser.close()
    return content
  }
}
