import puppeteer from 'puppeteer'

export default class Scrapper {
  static async getPageHTML(url, date = null) {
    const browser = await puppeteer.launch({ headless: false })
    const page = await browser.newPage()

    await page.goto(url, { waitUntil: 'networkidle2' })
    if (date) {
      await page.waitFor('#tb_date')
      await page.evaluate((date) => {
        document.querySelector('#tb_date').click()
        document.querySelector('#tb_date').value = date
        document
          .querySelector('#tb_date')
          .dispatchEvent({ code: 'Enter', key: 'Enter', charKode: 13, keyCode: 13, view: window })
      }, date)
    }

    const content = await page.content()
    console.log(content)
    //await browser.close()
    return content
  }
}
